import React from 'react';
import { Link } from 'react-router-dom';
import { events } from '../utils/mockEventData';
import { Badge } from '../components/ui/badge';
import { Card } from '../components/ui/card';
import { Button } from '../components/ui/button';
import SimpleSparkles from '../components/ui/SimpleSparkles';
import {
  Carousel,
  CarouselContent,
  CarouselItem,
  CarouselNext,
  CarouselPrevious,
} from '../components/ui/carousel';

function HomePage() {
  // Format price for display
  const formatPrice = (price) => {
    if (typeof price === 'object') {
      // Return the lowest price if price is an object with multiple tiers
      const prices = Object.values(price);
      return `$${Math.min(...prices)}`;
    }
    return price; // If price is already formatted
  };

  const renderEventCard = (event) => (
    <div className="w-full" key={event.id}>
      <Link to={`/event/${event.id}`}>
        <Card 
          className="h-full overflow-hidden hover:shadow-lg transition-shadow duration-200"
          glowEffect={true}
        >
          <div className="relative">
            <img 
              src={event.image} 
              alt={event.title} 
              className="w-full h-48 object-cover"
            />
            <div className="absolute top-2 right-2">
              <Badge variant="secondary">{event.category}</Badge>
            </div>
          </div>
          <div className="p-4">
            <h3 className="text-lg font-bold text-gray-900 dark:text-white truncate mb-1">{event.title}</h3>
            <div className="flex items-center text-gray-500 dark:text-gray-400 text-sm mb-2">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z" />
              </svg>
              <span>{event.date}</span>
            </div>
            <div className="flex items-center text-gray-500 dark:text-gray-400 text-sm mb-3">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-4 w-4 mr-1" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
              </svg>
              <span className="truncate">{event.location}</span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-blue-600 dark:text-blue-400 font-bold">
                ${Math.min(...Object.values(event.price))} - ${Math.max(...Object.values(event.price))}
              </span>
              <span className="text-xs text-gray-500 dark:text-gray-400">
                {event.availableSeats.reduce((sum, seat) => sum + seat.quantity, 0)} tickets left
              </span>
            </div>
          </div>
        </Card>
      </Link>
    </div>
  );

  return (
    <>
      <section className="relative mb-16 h-[300px] md:h-[400px] overflow-hidden rounded-lg">
        <SimpleSparkles 
          particleColor="#5D87FF"
          particleCount={70}
          minSize={0.6}
          maxSize={1.2}
          speed={0.5}
        />
        <div className="relative z-10 flex flex-col items-center justify-center h-full text-center px-4">
          <h1 className="text-6xl md:text-7xl lg:text-8xl font-bold text-white mb-4 animate-fade-in">
            TicketMaster
            <span className="ml-2 relative inline-block animate-pulse-slow">
              <span className="text-blue-400 glow-text">2.0</span>
            </span>
          </h1>
        </div>
        <style jsx="true">{`
          @keyframes fadeIn {
            from { opacity: 0; transform: translateY(20px); }
            to { opacity: 1; transform: translateY(0); }
          }

          @keyframes slowPulse {
            0% { opacity: 0.8; }
            50% { opacity: 1; }
            100% { opacity: 0.8; }
          }

          .animate-fade-in {
            animation: fadeIn 1.5s ease-out forwards;
          }

          .animate-pulse-slow {
            animation: slowPulse 3s infinite;
          }

          .glow-text {
            text-shadow: 0 0 10px rgba(96, 165, 250, 0.7),
                         0 0 20px rgba(96, 165, 250, 0.5),
                         0 0 30px rgba(96, 165, 250, 0.3);
          }
        `}</style>
      </section>

      <section className="mb-16">
        <div className="flex items-center mb-8">
          <h2 className="text-3xl font-bold text-gray-900 dark:text-white">
            All Events
          </h2>
        </div>

        <div className="relative px-8">
          <Carousel
            opts={{
              align: "start",
              loop: true,
            }}
            className="w-full relative">
            <CarouselContent className="-ml-4">
              {events.map(renderEventCard)}
            </CarouselContent>
            <CarouselPrevious />
            <CarouselNext />
          </Carousel>
        </div>
      </section>

      <section className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-16">
        <div className="bg-white dark:bg-gray-800 p-8 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200 border border-gray-200 dark:border-gray-700 shadow-[0_0_15px_rgba(87,165,255,0.2)] hover:shadow-[0_0_20px_rgba(87,165,255,0.4)]">
          <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">Discover Events</h2>
          <p className="text-gray-600 dark:text-gray-300">
            Find upcoming concerts, sports events, theater shows, and more.
          </p>
        </div>

        <div className="bg-white dark:bg-gray-800 p-8 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200 border border-gray-200 dark:border-gray-700 shadow-[0_0_15px_rgba(87,165,255,0.2)] hover:shadow-[0_0_20px_rgba(87,165,255,0.4)]">
          <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">Secure Tickets</h2>
          <p className="text-gray-600 dark:text-gray-300">
            Purchase tickets safely with our secure checkout process.
          </p>
        </div>

        <div className="bg-white dark:bg-gray-800 p-8 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200 border border-gray-200 dark:border-gray-700 shadow-[0_0_15px_rgba(87,165,255,0.2)] hover:shadow-[0_0_20px_rgba(87,165,255,0.4)]">
          <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">Manage Events</h2>
          <p className="text-gray-600 dark:text-gray-300">
            Keep track of your tickets and upcoming events in one place.
          </p>
        </div>
      </section>
    </>
  );
}

export default HomePage;
