import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button } from '../components/ui/button';
import { Card } from '../components/ui/card';
import { Badge } from '../components/ui/badge';

const MyTicketsPage = () => {
  const navigate = useNavigate();
  const [tickets, setTickets] = useState([]);
  const [showCancelModal, setShowCancelModal] = useState(false);
  const [ticketToCancel, setTicketToCancel] = useState(null);

  useEffect(() => {
    // Mock fetching tickets data
    const fetchTickets = async () => {
      try {
        // In a real app, this would be an API call
        /*
        * MICROSERVICE INTEGRATION POINT:
        * 
        * In production, you would fetch from the tickets microservice:
        * const response = await fetch('https://api-gateway.example.com/tickets/my-tickets', {
        *   headers: { Authorization: `Bearer ${token}` }
        * });
        * const data = await response.json();
        */

        // For demo purposes, use mock data immediately without loading state
        const mockTickets = [
          {
            id: 'TKT-1234-5678',
            eventTitle: 'Taylor Swift: The Eras Tour',
            eventDate: 'June 15, 2023',
            eventTime: '7:30 PM',
            location: 'MetLife Stadium, New Jersey',
            category: 'VIP',
            section: 'A',
            row: '1',
            seats: ['12', '13'],
            price: 450.00,
            purchaseDate: '2023-04-10',
            status: 'active',
            qrCode: 'https://api.qrserver.com/v1/create-qr-code/?size=150x150&data=TKT-1234-5678',
            eventImage: 'https://source.unsplash.com/random/800x500/?concert',
          },
          {
            id: 'TKT-8765-4321',
            eventTitle: 'Coldplay: Music of the Spheres',
            eventDate: 'August 22, 2023',
            eventTime: '8:00 PM',
            location: 'Wembley Stadium, London',
            category: 'CAT1',
            section: 'B',
            row: '5',
            seats: ['22'],
            price: 180.00,
            purchaseDate: '2023-05-15',
            status: 'active',
            qrCode: 'https://api.qrserver.com/v1/create-qr-code/?size=150x150&data=TKT-8765-4321',
            eventImage: 'https://source.unsplash.com/random/800x500/?music',
          },
          {
            id: 'TKT-9876-5432',
            eventTitle: 'NBA Finals 2023: Game 7',
            eventDate: 'June 18, 2023',
            eventTime: '9:00 PM',
            location: 'Madison Square Garden, New York',
            category: 'CAT2',
            section: 'Lower Bowl',
            row: '12',
            seats: ['5', '6', '7'],
            price: 350.00,
            purchaseDate: '2023-05-02',
            status: 'cancelled',
            refundAmount: 350.00,
            refundDate: '2023-05-10',
            qrCode: 'https://api.qrserver.com/v1/create-qr-code/?size=150x150&data=TKT-9876-5432',
            eventImage: 'https://source.unsplash.com/random/800x500/?basketball',
          }
        ];

        setTickets(mockTickets);
      } catch (error) {
        console.error('Error fetching tickets:', error);
      }
    };

    fetchTickets();
  }, []);

  const handleCancelClick = (ticket) => {
    setTicketToCancel(ticket);
    setShowCancelModal(true);
  };

  const confirmCancellation = async () => {
    try {
      // In a real app, this would be an API call to cancel the ticket
      /*
      * MICROSERVICE INTEGRATION POINT:
      * 
      * In production, you would call the tickets microservice:
      * const response = await fetch(`https://api-gateway.example.com/tickets/${ticketToCancel.id}/cancel`, {
      *   method: 'POST',
      *   headers: { 
      *     'Content-Type': 'application/json',
      *     Authorization: `Bearer ${token}` 
      *   }
      * });
      */

      // For demo purposes, update the local state
      setTickets(tickets.map(ticket => 
        ticket.id === ticketToCancel.id 
          ? { 
              ...ticket, 
              status: 'cancelled',
              refundAmount: ticket.price,
              refundDate: new Date().toISOString().split('T')[0]
            } 
          : ticket
      ));

      setShowCancelModal(false);

      // Navigate to success page
      navigate('/cancellation-success', { 
        state: { ticket: ticketToCancel }
      });
    } catch (error) {
      console.error('Error cancelling ticket:', error);
      setShowCancelModal(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-8">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white">My Tickets</h1>
        <Button 
          onClick={() => navigate('/')}
          variant="primary"
          className="font-bold"
        >
          Find More Events
        </Button>
      </div>

      {tickets.length === 0 ? (
        <div className="text-center py-12 bg-gray-100 dark:bg-gray-800 rounded-lg">
          <svg className="mx-auto h-12 w-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5H7a2 2 0 00-2 2v12a2 2 0 002 2h10a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2"></path>
          </svg>
          <h3 className="mt-2 text-lg font-medium text-gray-900 dark:text-white">No tickets found</h3>
          <p className="mt-1 text-gray-500 dark:text-gray-400">Browse events and purchase tickets to see them here.</p>
          <div className="mt-6">
            <Button 
              onClick={() => navigate('/')}
              variant="primary"
              className="font-bold"
            >
              Browse Events
            </Button>
          </div>
        </div>
      ) : (
        <div className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {tickets.map((ticket) => (
              <Card key={ticket.id} className={`overflow-hidden ${ticket.status === 'cancelled' ? 'opacity-70' : ''}`} glowEffect={true}>
                <div className="relative">
                  <img 
                    src={ticket.eventImage} 
                    alt={ticket.eventTitle} 
                    className="w-full h-40 object-cover"
                  />
                  <div className="absolute top-0 right-0 mt-3 mr-3">
                    <Badge 
                      className={`${
                        ticket.status === 'active' ? 'bg-green-600' : 
                        ticket.status === 'cancelled' ? 'bg-red-600' : 'bg-yellow-600'
                      } text-white px-2 py-1 text-xs font-medium uppercase rounded`}
                    >
                      {ticket.status}
                    </Badge>
                  </div>
                </div>

                <div className="p-4">
                  <h3 className="text-lg font-bold text-gray-900 dark:text-white mb-1">{ticket.eventTitle}</h3>

                  <div className="flex items-center text-gray-500 dark:text-gray-400 text-sm mb-2">
                    <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 7V3m8 4V3m-9 8h10M5 21h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v12a2 2 0 002 2z"></path>
                    </svg>
                    {ticket.eventDate} at {ticket.eventTime}
                  </div>

                  <div className="flex items-center text-gray-500 dark:text-gray-400 text-sm mb-3">
                    <svg className="w-4 h-4 mr-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z"></path>
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 11a3 3 0 11-6 0 3 3 0 016 0z"></path>
                    </svg>
                    {ticket.location}
                  </div>

                  <div className="border-t border-gray-200 dark:border-gray-700 my-3"></div>

                  <div className="flex justify-between mb-2">
                    <span className="text-gray-500 dark:text-gray-400 text-sm">Category</span>
                    <span className="text-gray-900 dark:text-white text-sm font-medium">{ticket.category}</span>
                  </div>

                  <div className="flex justify-between mb-2">
                    <span className="text-gray-500 dark:text-gray-400 text-sm">Section / Row</span>
                    <span className="text-gray-900 dark:text-white text-sm font-medium">{ticket.section} / {ticket.row}</span>
                  </div>

                  <div className="flex justify-between mb-2">
                    <span className="text-gray-500 dark:text-gray-400 text-sm">Seats</span>
                    <span className="text-gray-900 dark:text-white text-sm font-medium">{ticket.seats.join(', ')}</span>
                  </div>

                  <div className="flex justify-between mb-3">
                    <span className="text-gray-500 dark:text-gray-400 text-sm">Purchase Date</span>
                    <span className="text-gray-900 dark:text-white text-sm font-medium">{ticket.purchaseDate}</span>
                  </div>

                  {ticket.status === 'cancelled' && (
                    <>
                      <div className="border-t border-gray-200 dark:border-gray-700 my-3"></div>
                      <div className="flex justify-between mb-2">
                        <span className="text-gray-500 dark:text-gray-400 text-sm">Refund Amount</span>
                        <span className="text-green-600 dark:text-green-500 text-sm font-medium">${ticket.refundAmount.toFixed(2)}</span>
                      </div>
                      <div className="flex justify-between mb-2">
                        <span className="text-gray-500 dark:text-gray-400 text-sm">Refund Date</span>
                        <span className="text-gray-900 dark:text-white text-sm font-medium">{ticket.refundDate}</span>
                      </div>
                    </>
                  )}

                  <div className="border-t border-gray-200 dark:border-gray-700 my-3"></div>

                  <div className="flex space-x-2">
                    <Button 
                      onClick={() => window.open(ticket.qrCode, '_blank')}
                      variant="outline"
                      className="flex-1 h-9"
                    >
                      <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 4v1m6 11h2m-6 0h-2v4m0-11v3m0 0h.01M12 12h4.01M16 20h4M4 12h4m12 0h.01M5 8h2a1 1 0 001-1V5a1 1 0 00-1-1H5a1 1 0 00-1 1v2a1 1 0 001 1zm12 0h2a1 1 0 001-1V5a1 1 0 00-1-1h-2a1 1 0 00-1 1v2a1 1 0 001 1zM5 20h2a1 1 0 001-1v-2a1 1 0 00-1-1H5a1 1 0 00-1 1v2a1 1 0 001 1z"></path>
                      </svg>
                      View QR
                    </Button>

                    {ticket.status === 'active' && (
                      <>
                        <Button 
                          onClick={() => navigate('/trading', { state: { ticket } })}
                          variant="outline"
                          className="flex-1 h-9"
                        >
                          <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M8 7h12m0 0l-4-4m4 4l-4 4m0 6H4m0 0l4 4m-4-4l4-4"></path>
                          </svg>
                          Trade
                        </Button>

                        <Button 
                          onClick={() => handleCancelClick(ticket)}
                          variant="outline"
                          className="flex-1 h-9"
                        >
                          <svg className="w-4 h-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12"></path>
                          </svg>
                          Cancel
                        </Button>
                      </>
                    )}
                  </div>
                </div>
              </Card>
            ))}
          </div>
        </div>
      )}

      {/* Cancellation Confirmation Modal */}
      {showCancelModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50 p-4">
          <div className="bg-white dark:bg-gray-800 rounded-lg p-6 max-w-md w-full">
            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">
              Confirm Cancellation
            </h3>
            <p className="text-gray-700 dark:text-gray-300 mb-6">
              Are you sure you want to cancel your ticket for "<span className="font-medium">{ticketToCancel?.eventTitle}</span>"? You will receive a refund of ${ticketToCancel?.price.toFixed(2)}.
            </p>
            <div className="flex gap-4 justify-end">
              <Button 
                onClick={() => setShowCancelModal(false)}
                variant="outline"
                className="font-medium"
              >
                Keep Ticket
              </Button>
              <Button 
                onClick={confirmCancellation}
                variant="default"
                className="bg-red-600 hover:bg-red-700 text-white font-medium"
              >
                Yes, Cancel Ticket
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MyTicketsPage;
