import React from 'react';
import Spinner from './ui/spinner';

const LoadingOverlay = ({ isLoading = false, fullscreen = false }) => {
  if (!isLoading) return null;

  return (
    <div className={`
      ${fullscreen ? 'fixed inset-0 z-50' : 'absolute inset-0 z-10'}
      bg-gradient-to-br from-gray-900/90 to-black/95
      backdrop-blur-sm flex items-center justify-center
      transition-opacity duration-300
    `}>
      <div className="flex flex-col items-center">
        <Spinner 
          size={fullscreen ? 120 : 80}
          thickness={fullscreen ? 6 : 4}
          primaryColor="#57A5FF" 
          secondaryColor="#FF5757"
          backgroundColor="rgba(255,255,255,0.15)"
        />
        <div className="mt-4 text-white font-medium text-lg opacity-80">
          Loading...
        </div>
      </div>
    </div>
  );
};

export default LoadingOverlay;
