import React from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

function ProtectedRoute() {
  const { isAuthenticated, loading, isDevMode } = useAuth();

  // If still loading, show a loading indicator
  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  // If not authenticated and we're not in dev mode, redirect to login page
  if (!isAuthenticated) {
    // In dev mode, show a message rather than redirecting
    if (isDevMode) {
      return (
        <div className="min-h-screen flex flex-col items-center justify-center">
          <div className="bg-gray-800 p-8 rounded-lg shadow-lg max-w-md w-full">
            <h2 className="text-2xl font-bold text-white mb-4">Authentication Required</h2>
            <p className="text-gray-300 mb-6">You need to be logged in to view this page.</p>
            <p className="text-gray-300 mb-6">Click the "Dev Login" button in the navbar to login with a demo account.</p>
          </div>
        </div>
      );
    }
    return <Navigate to="/login" replace />;
  }

  // If authenticated, render the nested routes
  return <Outlet />;
}

export default ProtectedRoute;
