import React, { useEffect, useRef } from 'react';

const Spinner = ({ 
  size = 120, 
  thickness = 6,
  duration = 1.5,
  className = '',
  primaryColor = '#57A5FF',
  secondaryColor = '#FF5757',
  backgroundColor = 'rgba(255,255,255,0.1)'
}) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    const centerX = size / 2;
    const centerY = size / 2;
    const radius = (size - thickness) / 2 - 2;

    let rotation = 0;
    let animationFrameId;

    // Create gradient colors
    const createGradient = (rotation) => {
      const gradient = ctx.createLinearGradient(
        centerX + Math.cos(rotation) * radius,
        centerY + Math.sin(rotation) * radius,
        centerX + Math.cos(rotation + Math.PI) * radius,
        centerY + Math.sin(rotation + Math.PI) * radius
      );
      gradient.addColorStop(0, primaryColor);
      gradient.addColorStop(1, secondaryColor);
      return gradient;
    };

    const render = () => {
      ctx.clearRect(0, 0, size, size);

      // Draw background circle
      ctx.beginPath();
      ctx.arc(centerX, centerY, radius, 0, Math.PI * 2);
      ctx.strokeStyle = backgroundColor;
      ctx.lineWidth = thickness;
      ctx.stroke();

      // Calculate start and end angles for spinner arc
      const startAngle = rotation;
      const endAngle = rotation + Math.PI * 1.5;

      // Draw spinner arc
      ctx.beginPath();
      ctx.arc(centerX, centerY, radius, startAngle, endAngle);
      ctx.strokeStyle = createGradient(rotation);
      ctx.lineWidth = thickness;
      ctx.lineCap = 'round';
      ctx.stroke();

      // Update rotation for animation
      rotation += (Math.PI * 2) / (60 * duration);
      if (rotation >= Math.PI * 2) {
        rotation = 0;
      }

      animationFrameId = requestAnimationFrame(render);
    };

    render();

    return () => {
      cancelAnimationFrame(animationFrameId);
    };
  }, [size, thickness, duration, primaryColor, secondaryColor, backgroundColor]);

  return (
    <div className={`flex items-center justify-center ${className}`}>
      <canvas 
        ref={canvasRef} 
        width={size} 
        height={size} 
        className="animate-pulse-subtle"
        style={{ 
          width: size, 
          height: size,
          filter: 'drop-shadow(0 0 6px rgba(0, 123, 255, 0.3))'
        }}
      />
      <style jsx="true">{`
        @keyframes pulse-subtle {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.8; }
        }
        .animate-pulse-subtle {
          animation: pulse-subtle 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
        }
      `}</style>
    </div>
  );
};

export default Spinner;
