import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Navbar = () => {
  const location = useLocation();
  const { user, logout, isAuthenticated, isDevMode, devModeLogin } = useAuth();

  // Simple active link checker
  const isActive = (path) => {
    return location.pathname === path;
  };

  // Emergency login using the context method
  const handleEmergencyLogin = async () => {
    try {
      await devModeLogin('emergency@example.com');
      console.log('Emergency login successful');
    } catch (error) {
      console.error('Error in emergency login:', error);
      alert('Login failed: ' + error.message);
    }
  };

  // Simple logout that clears localStorage and refreshes
  const handleLogout = async () => {
    try {
      await logout();
      console.log('Logout successful');
    } catch (error) {
      console.error('Error logging out:', error);
      alert('Logout failed: ' + error.message);
    }
  };

  return (
    <nav className="bg-gray-900 text-white p-4">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-xl font-bold text-yellow-400">
          Ticketmaster
        </Link>

        <div className="flex items-center space-x-4">
          <Link 
            to="/" 
            className={`px-3 py-2 ${isActive('/') ? 'text-yellow-400' : 'text-white'}`}
          >
            Home
          </Link>

          {isAuthenticated ? (
            <>
              <Link 
                to="/my-tickets" 
                className={`px-3 py-2 ${isActive('/my-tickets') ? 'text-yellow-400' : 'text-white'}`}
              >
                My Tickets
              </Link>
              <Link 
                to="/trading" 
                className={`px-3 py-2 ${isActive('/trading') ? 'text-yellow-400' : 'text-white'}`}
              >
                Trading
              </Link>

              <button 
                onClick={handleLogout}
                className="ml-4 px-3 py-1 bg-red-600 text-white rounded"
              >
                Logout
              </button>
            </>
          ) : (
            <>
              <Link 
                to="/login" 
                className={`px-3 py-2 ${isActive('/login') ? 'text-yellow-400' : 'text-white'}`}
              >
                Login
              </Link>
              <Link 
                to="/register" 
                className={`px-3 py-2 ${isActive('/register') ? 'text-yellow-400' : 'text-white'}`}
              >
                Register
              </Link>

              {isDevMode && (
                <button 
                  onClick={handleEmergencyLogin}
                  className="ml-4 px-3 py-1 bg-green-600 text-white rounded"
                >
                  Emergency Login
                </button>
              )}
            </>
          )}

          {/* Debug link - always visible in dev mode */}
          {isDevMode && (
            <Link 
              to="/debug" 
              className={`ml-2 px-3 py-1 bg-purple-600 text-white rounded ${isActive('/debug') ? 'ring-2 ring-white' : ''}`}
            >
              Debug
            </Link>
          )}
        </div>
      </div>

      {isDevMode && (
        <div className="bg-yellow-600 text-white text-center text-xs p-1">
          Development Mode {isAuthenticated && `• Logged in as ${user?.email || 'User'}`}
        </div>
      )}
    </nav>
  );
};

export default Navbar;
