import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import './App.css';
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import ProfilePage from './pages/ProfilePage';
import EventDetailsPage from './pages/EventDetailsPage';
import CheckoutPage from './pages/CheckoutPage';
import PaymentConfirmationPage from './pages/PaymentConfirmationPage';
import MyTicketsPage from './pages/MyTicketsPage';
import TradingPage from './pages/TradingPage';
import CancellationSuccessPage from './pages/CancellationSuccessPage';
import TradeSuccessPage from './pages/TradeSuccessPage';
import Navbar from './components/Navbar';
import PageLayout from './components/PageLayout';
import ProtectedRoute from './components/ProtectedRoute';
import { AuthProvider, useAuth } from './context/AuthContext';
import LoadingOverlay from './components/LoadingOverlay';

function App() {
  const { isAuthenticated } = useAuth();
  const location = useLocation();
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // Force dark mode
    document.documentElement.classList.add('dark');
  }, []);

  // Add page transition loading effect
  useEffect(() => {
    // Start loading animation
    setLoading(true);

    // Simulate page loading time (min 400ms, max 1000ms)
    const timer = setTimeout(() => {
      setLoading(false);
    }, Math.max(400, Math.min(1000, Math.random() * 800)));

    return () => clearTimeout(timer);
  }, [location.pathname]);

  // Wrapper function to apply PageLayout to all routes
  const withLayout = (Component) => (
    <PageLayout>
      <Component />
    </PageLayout>
  );

  // Wrapper for protected routes with layout
  const protectedRouteWithLayout = (Component) => (
    <ProtectedRoute isAuthenticated={isAuthenticated}>
      <PageLayout>
        <Component />
      </PageLayout>
    </ProtectedRoute>
  );

  return (
    <AuthProvider>
      <Router>
        <Navbar />
        {loading && <LoadingOverlay isLoading={true} fullscreen={true} />}
        <Routes>
          {/* Public routes */}
          <Route path="/" element={withLayout(HomePage)} />
          <Route path="/event/:id" element={withLayout(EventDetailsPage)} />
          <Route path="/login" element={withLayout(LoginPage)} />
          <Route path="/register" element={withLayout(RegisterPage)} />

          {/* Protected routes */}
          <Route element={<ProtectedRoute isAuthenticated={isAuthenticated} />}>
            <Route path="/my-tickets" element={protectedRouteWithLayout(MyTicketsPage)} />
            <Route path="/profile" element={protectedRouteWithLayout(ProfilePage)} />
            <Route path="/trading" element={protectedRouteWithLayout(TradingPage)} />
          </Route>

          {/* Catch all - redirect to home */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </Router>
    </AuthProvider>
  );
}

export default App;
