import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Button } from '../components/ui/button';
import { Card } from '../components/ui/card';
import { Badge } from '../components/ui/badge';

const TradingPage = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [userTickets, setUserTickets] = useState([]);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [availableTickets, setAvailableTickets] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [showTradeModal, setShowTradeModal] = useState(false);
  const [ticketToTrade, setTicketToTrade] = useState(null);
  const availableTicketsRef = useRef(null);

  // Fetch user's tickets
  useEffect(() => {
    const fetchUserTickets = async () => {
      setIsLoading(true);

      try {
        // For demo purposes, use mock data with a short delay
        setTimeout(() => {
          const mockTickets = [
            {
              id: 'TKT-1234-5678',
              eventTitle: 'Taylor Swift: The Eras Tour',
              eventDate: 'June 15, 2023',
              eventTime: '7:30 PM',
              location: 'MetLife Stadium, New Jersey',
              category: 'VIP',
              section: 'A',
              row: '1',
              seats: ['12', '13'],
              price: 450.00,
              purchaseDate: '2023-04-10',
              status: 'active',
              qrCode: 'https://api.qrserver.com/v1/create-qr-code/?size=150x150&data=TKT-1234-5678',
              eventImage: 'https://source.unsplash.com/random/800x500/?concert',
            },
            {
              id: 'TKT-8765-4321',
              eventTitle: 'Coldplay: Music of the Spheres',
              eventDate: 'August 22, 2023',
              eventTime: '8:00 PM',
              location: 'Wembley Stadium, London',
              category: 'CAT1',
              section: 'B',
              row: '5',
              seats: ['22'],
              price: 180.00,
              purchaseDate: '2023-05-15',
              status: 'active',
              qrCode: 'https://api.qrserver.com/v1/create-qr-code/?size=150x150&data=TKT-8765-4321',
              eventImage: 'https://source.unsplash.com/random/800x500/?music',
            }
          ];

          // If a ticket was passed via location state, pre-select it
          if (location.state?.ticket) {
            const preSelectedTicket = location.state.ticket;
            setUserTickets(mockTickets);

            // Find the matching ticket in our mock data or use the passed one
            const matchedTicket = mockTickets.find(t => t.id === preSelectedTicket.id) || preSelectedTicket;
            setSelectedTicket(matchedTicket);
            fetchAvailableTicketsForTrade(matchedTicket);
          } else {
            setUserTickets(mockTickets);
            setIsLoading(false);
          }
        }, 300);

      } catch (error) {
        console.error('Error fetching user tickets:', error);
        setIsLoading(false);
      }
    };

    fetchUserTickets();
  }, [location.state]);

  // Fetch available tickets for trade
  const fetchAvailableTicketsForTrade = useCallback((ticket) => {
    setIsLoading(true);

    // In a real app, this would be an API call
    /*
    * MICROSERVICE INTEGRATION POINT:
    * 
    * In production, you would fetch from the trading microservice:
    * const response = await fetch(`https://api-gateway.example.com/trading/available-tickets?category=${category}&eventTitle=${eventTitle}`, {
    *   headers: { Authorization: `Bearer ${token}` }
    * });
    * const data = await response.json();
    */

    // For demo purposes, use mock data with a short delay to ensure state is ready
    setTimeout(() => {
      const mockAvailableTickets = [
        {
          id: 'TKT-2468-1357',
          eventTitle: 'Taylor Swift: The Eras Tour',
          eventDate: 'June 18, 2023',
          eventTime: '7:30 PM',
          location: 'MetLife Stadium, New Jersey',
          category: location.state?.ticket?.category || category,
          section: 'B',
          row: '3',
          seats: ['5'],
          price: 450.00,
          ownerName: 'John Smith',
          ownerRating: 4.8,
          tradePreference: 'same-event',
          eventImage: 'https://source.unsplash.com/random/800x500/?concert'
        },
        {
          id: 'TKT-1357-2468',
          eventTitle: 'Taylor Swift: The Eras Tour',
          eventDate: 'June 20, 2023',
          eventTime: '7:30 PM',
          location: 'MetLife Stadium, New Jersey',
          category: location.state?.ticket?.category || category,
          section: 'A',
          row: '5',
          seats: ['20'],
          price: 450.00,
          ownerName: 'Emily Johnson',
          ownerRating: 4.9,
          tradePreference: 'any',
          eventImage: 'https://source.unsplash.com/random/800x500/?concert'
        }
      ].filter(ticket => 
        // Filter to only include tickets from the same event and category
        ticket.eventTitle === eventTitle && 
        ticket.category === category
      );

      setAvailableTickets(mockAvailableTickets);
      setIsLoading(false);
    }, 300); // Small delay to ensure component is fully mounted
  }, [location.state]);

  useEffect(() => {
    setIsLoading(true);

    // Get the user's ticket from the navigation state
    if (location.state?.ticket) {
      setUserTicket(location.state.ticket);
      fetchAvailableTickets(location.state.ticket.category, location.state.ticket.eventTitle);
    } else {
      // Create a mock ticket for demo purposes if no ticket in state
      // In a real app, you would redirect or fetch user tickets
      const mockUserTicket = {
        id: 'TKT-1234-5678',
        eventTitle: 'Taylor Swift: The Eras Tour',
        eventDate: 'May 15, 2023',
        eventTime: '8:00 PM',
        location: 'MetLife Stadium, New Jersey',
        category: 'VIP',
        section: 'A',
        row: '1',
        seats: ['12'],
        price: 550.00,
        eventImage: 'https://source.unsplash.com/random/800x500/?concert'
      };

      setTimeout(() => {
        setUserTicket(mockUserTicket);
        fetchAvailableTickets(mockUserTicket.category, mockUserTicket.eventTitle);
      }, 300);

      // Set a message or notification that this is demo mode
      console.log("Trading page opened in demo mode - no ticket was selected");
    }
  }, [location, navigate, fetchAvailableTickets]);

  const handleTradeClick = (ticket) => {
    setSelectedTicket(ticket);
    setShowTradeModal(true);
  };

  const confirmTrade = async () => {
    try {
      // In a real app, this would be an API call to process the trade
      /*
      * MICROSERVICE INTEGRATION POINT:
      * 
      * In production, you would call the trading microservice:
      * const response = await fetch('https://api-gateway.example.com/trading/process', {
      *   method: 'POST',
      *   headers: { 
      *     'Content-Type': 'application/json',
      *     Authorization: `Bearer ${token}` 
      *   },
      *   body: JSON.stringify({
      *     userTicketId: userTicket.id,
      *     tradeTicketId: selectedTicket.id
      *   })
      * });
      */

      // For demo purposes, go to success page
      setShowTradeModal(false);
      navigate('/trade-success', { 
        state: { 
          tradeData: {
            eventName: userTicket.eventTitle,
            tradedTickets: {
              category: userTicket.category,
              count: userTicket.seats.length
            },
            receivedTickets: {
              category: selectedTicket.category,
              count: selectedTicket.seats.length
            },
            tradeDate: new Date().toLocaleDateString()
          }
        }
      });
    } catch (error) {
      console.error('Error processing trade:', error);
      setShowTradeModal(false);
    }
  };

  // Show a loading state while data is being prepared
  if (isLoading || !userTicket) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Ticket Trading</h1>
          <Button 
            onClick={() => navigate('/my-tickets')}
            className="bg-yellow-500 hover:bg-yellow-600 text-black font-bold"
          >
            Back to My Tickets
          </Button>
        </div>
        <div className="flex justify-center items-center h-64">
          <p className="text-lg text-gray-500 dark:text-gray-400">Loading trading options...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Ticket Trading</h1>
        <Button 
          onClick={() => navigate('/my-tickets')}
          className="bg-yellow-500 hover:bg-yellow-600 text-black font-bold"
        >
          Back to My Tickets
        </Button>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
        <div className="lg:col-span-1">
          <Card className="overflow-hidden bg-gray-800 border border-blue-500" glowEffect={true}>
            <div className="p-4">
              <h2 className="text-xl font-semibold text-blue-400 mb-4">Your Ticket</h2>
              <div className="mb-2">
                <h3 className="text-lg font-medium text-white">{userTicket.eventTitle}</h3>
                <p className="text-gray-300">{userTicket.eventDate} at {userTicket.eventTime}</p>
                <p className="text-gray-300 mb-3">{userTicket.location}</p>
              </div>

              <div className="border-t border-blue-500/30 my-3"></div>

              <div className="flex justify-between mb-1">
                <span className="text-gray-300">Category:</span>
                <span className="text-white font-medium">{userTicket.category}</span>
              </div>
              <div className="flex justify-between mb-1">
                <span className="text-gray-300">Section:</span>
                <span className="text-white font-medium">{userTicket.section}</span>
              </div>
              <div className="flex justify-between mb-1">
                <span className="text-gray-300">Row:</span>
                <span className="text-white font-medium">{userTicket.row}</span>
              </div>
              <div className="flex justify-between mb-1">
                <span className="text-gray-300">Seats:</span>
                <span className="text-white font-medium">{userTicket.seats.join(', ')}</span>
              </div>
              <div className="flex justify-between mb-3">
                <span className="text-gray-300">Value:</span>
                <span className="text-blue-400 font-bold">${userTicket.price.toFixed(2)}</span>
              </div>

              <div className="border-t border-blue-500/30 my-3"></div>

              <div className="text-xs text-gray-400 mb-4">
                <p className="mb-2">Trading Rules:</p>
                <ul className="list-disc list-inside space-y-1">
                  <li>You can only trade tickets from the same event.</li>
                  <li>Tickets must be in the same category.</li>
                  <li>Both parties must agree to the trade.</li>
                  <li>No refunds after a trade is completed.</li>
                </ul>
              </div>
            </div>
          </Card>
        </div>

        <div className="lg:col-span-2">
          <div className="bg-gray-100 dark:bg-gray-800 p-4 rounded-lg mb-4">
            <h2 className="text-xl font-semibold text-gray-900 dark:text-white mb-4">
              Available Trades for {userTicket.eventTitle} - {userTicket.category} Category
            </h2>
          </div>

          {availableTickets.length === 0 ? (
            <div className="bg-gray-100 dark:bg-gray-800 p-8 text-center rounded-lg">
              <svg className="w-16 h-16 text-gray-400 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9.172 16.172a4 4 0 015.656 0M9 10h.01M15 10h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
              </svg>
              <p className="text-gray-500 dark:text-gray-400 mb-4">
                No tickets available for trade in this category and event.
              </p>
              <Button 
                onClick={() => navigate('/my-tickets')}
                className="bg-blue-600 hover:bg-blue-700 text-white"
              >
                Back to My Tickets
              </Button>
            </div>
          ) : (
            <div className="grid grid-cols-1 gap-4">
              {availableTickets.map(ticket => (
                <Card key={ticket.id} className="overflow-hidden dark:bg-gray-700" glowEffect={true}>
                  <div className="grid grid-cols-1 md:grid-cols-3 h-full">
                    <div className="md:col-span-1">
                      <div className="h-full relative">
                        <img 
                          src={ticket.eventImage} 
                          alt={ticket.eventTitle} 
                          className="w-full h-full object-cover aspect-square md:aspect-auto"
                          style={{ maxHeight: '220px' }}
                        />
                        <div className="absolute bottom-2 left-2">
                          <div className="bg-blue-600 text-white text-xs px-2 py-1 rounded font-medium">
                            {ticket.category}
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="md:col-span-2 p-4 flex flex-col">
                      <div className="flex justify-between">
                        <h3 className="text-lg font-medium text-gray-900 dark:text-white mb-1">{ticket.eventTitle}</h3>
                        <span className="text-blue-600 dark:text-blue-400 font-bold">${ticket.price.toFixed(2)}</span>
                      </div>

                      <p className="text-gray-500 dark:text-gray-400 text-sm mb-1">{ticket.eventDate} at {ticket.eventTime}</p>
                      <p className="text-gray-500 dark:text-gray-400 text-sm mb-2">{ticket.location}</p>

                      <div className="text-sm mb-4">
                        <div className="flex flex-wrap gap-4">
                          <div>
                            <span className="text-gray-500 dark:text-gray-400">Section:</span>
                            <span className="text-gray-700 dark:text-gray-300 ml-1 font-medium">{ticket.section}</span>
                          </div>
                          <div>
                            <span className="text-gray-500 dark:text-gray-400">Row:</span>
                            <span className="text-gray-700 dark:text-gray-300 ml-1 font-medium">{ticket.row}</span>
                          </div>
                          <div>
                            <span className="text-gray-500 dark:text-gray-400">Seats:</span>
                            <span className="text-gray-700 dark:text-gray-300 ml-1 font-medium">{ticket.seats.join(', ')}</span>
                          </div>
                        </div>
                      </div>

                      <div className="flex items-center justify-between mt-auto">
                        <div className="flex items-center">
                          <span className="text-sm text-gray-500 dark:text-gray-400 mr-2">Owner: {ticket.ownerName}</span>
                          <div className="flex items-center">
                            <span className="text-sm text-yellow-500 mr-1">{ticket.ownerRating}</span>
                            <svg className="w-4 h-4 text-yellow-500" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">
                              <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z"></path>
                            </svg>
                          </div>
                        </div>

                        <Button 
                          onClick={() => handleTradeClick(ticket)}
                          className="bg-blue-600 hover:bg-blue-700 text-white"
                        >
                          Trade for This Ticket
                        </Button>
                      </div>
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* Trade Confirmation Modal */}
      {showTradeModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50 p-4">
          <div className="bg-white dark:bg-gray-800 rounded-lg p-6 max-w-lg w-full">
            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-4">
              Confirm Trade Request
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
              <div className="bg-gray-100 dark:bg-gray-700 p-3 rounded-lg">
                <h4 className="font-medium text-gray-900 dark:text-white text-sm mb-2">Your Ticket:</h4>
                <p className="text-gray-800 dark:text-gray-200 font-medium">{userTicket.eventTitle}</p>
                <p className="text-gray-600 dark:text-gray-300 text-sm">{userTicket.eventDate}</p>
                <p className="text-gray-600 dark:text-gray-300 text-sm mb-2">
                  Section {userTicket.section}, Row {userTicket.row}, Seat(s) {userTicket.seats.join(', ')}
                </p>
                <p className="text-blue-600 dark:text-blue-400 font-medium">${userTicket.price.toFixed(2)}</p>
              </div>

              <div className="bg-gray-100 dark:bg-gray-700 p-3 rounded-lg">
                <h4 className="font-medium text-gray-900 dark:text-white text-sm mb-2">Trade For:</h4>
                <p className="text-gray-800 dark:text-gray-200 font-medium">{selectedTicket.eventTitle}</p>
                <p className="text-gray-600 dark:text-gray-300 text-sm">{selectedTicket.eventDate}</p>
                <p className="text-gray-600 dark:text-gray-300 text-sm mb-2">
                  Section {selectedTicket.section}, Row {selectedTicket.row}, Seat(s) {selectedTicket.seats.join(', ')}
                </p>
                <p className="text-blue-600 dark:text-blue-400 font-medium">${selectedTicket.price.toFixed(2)}</p>
              </div>
            </div>

            <div className="bg-blue-100 dark:bg-blue-900/20 p-3 rounded-lg mb-6">
              <div className="flex items-start">
                <svg className="w-5 h-5 text-blue-600 dark:text-blue-500 mr-2 mt-0.5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"></path>
                </svg>
                <div>
                  <p className="text-blue-800 dark:text-blue-200 text-sm font-medium">Important Information</p>
                  <p className="text-blue-700 dark:text-blue-300 text-xs">
                    By confirming this trade, you're initiating a request to trade your ticket. The other ticket owner will need to approve this request. Once approved, the trade is final and cannot be reversed.
                  </p>
                </div>
              </div>
            </div>

            <div className="flex gap-4 justify-end">
              <Button 
                onClick={() => setShowTradeModal(false)}
                variant="outline"
                className="font-medium"
              >
                Cancel
              </Button>
              <Button 
                onClick={confirmTrade}
                variant="primary"
                className="font-bold"
              >
                Confirm Trade Request
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TradingPage;
