import React, { useEffect } from 'react';
import { Navigate, useLocation } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

function ProtectedRoute({ children }) {
  const { user, isAuthenticated, isDevMode, loading, devModeLogin } = useAuth();
  const location = useLocation();

  useEffect(() => {
    // Log authentication state for debugging
    console.log('ProtectedRoute - Auth state:', { 
      isAuthenticated, 
      isDevMode, 
      path: location.pathname,
      user: user?.email
    });

    // Perform auto-login once when we're in dev mode and not authenticated
    if (isDevMode && !isAuthenticated && !loading) {
      console.log("ProtectedRoute - Attempting auto-login in dev mode");
      devModeLogin('auto@example.com').catch(e => 
        console.error("Auto-login failed:", e)
      );
    }
  }, [isAuthenticated, isDevMode, location.pathname, user, loading, devModeLogin]);

  // Handle loading state
  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-yellow-400 mx-auto"></div>
          <p className="mt-4 text-lg">Loading...</p>
        </div>
      </div>
    );
  }

  // If user is authenticated, render children
  if (isAuthenticated) {
    return children;
  }

  // Otherwise, redirect to login
  return <Navigate to="/login" state={{ from: location }} replace />;
}

export default ProtectedRoute;
